import React from "react";
import {Link} from "react-router-dom";

class Posts extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        posts: [],
      };
    }

    componentDidMount() {
        const url = "/api/v1/posts/index";
        fetch(url)
            .then(response => {
            if (response.ok) {
                return response.json();
            }
            throw new Error("Network response was not ok.");
            })
            .then(response => this.setState({ posts: response }))
            .catch(() => this.props.history.push("/"));
    }

    render() {
        const { posts } = this.state;
        const allPosts = posts.map((post, index) => (
          <div key={index} className="col-md-6 col-lg-4">
            <div className="card mb-4">
              <div className="card-body">
                <h5 className="card-title">{post.title}</h5>
                <Link to={`/post/${post.id}`} className="btn custom-button">
                  View Post
                </Link>
              </div>
            </div>
          </div>
        ));

        const noPost = (
          <div className="vw-100 vh-50 d-flex align-items-center justify-content-center">
            <h4>
              No Posts yet. Lets create one!
            </h4>
          </div>
        );

        const loggedin = (
          <div className="d-flex align-items-center float-right">
            <h4>
            <Link to="/logout" className="btn custom-button">Log Out</Link>
            </h4>
          </div>
        );

        const loggedout = (
          <div className="d-flex align-items-center float-right">
            <h4>
            <Link to="/login" className="btn custom-button">Log In</Link>
            </h4>
            <h4>
            <Link to="/signup" className="btn custom-button">Sign Up</Link>
            </h4>
          </div>
        );

        return (
          <>
            <div>{false ? loggedin : loggedout}</div>
            <section className="jumbotron jumbotron-fluid text-center">
              <div className="container py-5">
                <h1 className="display-4">Your Blog Posts</h1>
                <p className="lead text-muted">
                  Here are your blog posts.
                </p>
              </div>
            </section>
            <div className="py-5">
              <main className="container"> 
                <div className="text-right mb-1">
                  <Link to="/post" className="btn custom-button">
                    Create New Post
                  </Link>
                </div>
                <div className="row">
                  {posts.length > 0 ? allPosts : noPost}
                </div>
                <Link to="/" className="btn btn-link">
                  Home
                </Link>
              </main>
            </div>
          </>
        );
      }
  }

  export default Posts;
